INSERT INTO `v4_wp_posts` VALUES (53, 2, '2006-10-08 14:53:02', '2006-10-08 12:53:02', '<p><img style="margin: 5px" height="82" src="http://www.kreisquadratur.de/blog/wp-content/uploads/AloeVera_CF80/AloeVera_JG2.jpg" width="69" class="reflex itiltnone alignright"/> Kurz eingeworfen: Eigentlich egal, ob man diese <em>Aloe-Vera</em>, <em>Sensitive</em>, <em>-1%-Fett </em>-Hypes mag oder nicht, komplett unabhängig davon: Es schmeckt einfach lecker morgens zum Frühstück. Nicht zu süß und nicht zu geschmacklos.<br />Warum dann jedoch im <a href="http://www.twin.ch/de/showcase/spots/aloevera.php">Werbespot</a> dieser lippenaufgespritze Angelie Julie Verschnitt vorkommen muss, nun ja.<br />Gemeint ist der <a href="http://www.emmi.ch/deutsch/molkereiprodukte/jogurt/aloe-vera/">Jogurt von Emmi</a> ((Originalwebseite von <a href="http://www.emmi-aloe-vera.ch/">Emmi Aloe Vera</a>)).</p>', 'Aloe Vera', 0, '', 'publish', 'open', 'open', '', 'aloe-vera', '', '\nArray', '2008-08-26 21:09:27', '2008-08-26 19:09:27', '', 0, 'http://www.kreisquadratur.de/blog/?p=53', 0, 'post', '', 0); 
