INSERT INTO `v4_wp_posts` VALUES (97, 2, '2008-03-02 14:59:26', '2008-03-02 12:59:26', '<p>Mail<br />\r\nIn <em>Mail</em> kann in den Einstellungen festlegen, das ausgehende E-Mails in Klartext (plain text) geschrieben werden. Es gibt jedoch leider keine Einstellung, um eingehende E-Mails auch als Klartext statt <em>RTF</em> oder <em>HTML</em> anzeigen zu lassen. Dies ist jedoch über das <em>Terminal</em> möglich, indem man eine Einstellung in die Einstellungen schreibt:<br />\r\n<code>defaults write com.apple.mail PreferPlainText -bool TRUE</code><br />\r\nDie Datei <em>com.apple.mail.plist</em> findet man in der Library des Benutzers (<em>/Users/Benutzername/Library/Preferences/</em>)</p>\r\n', 'Software', 0, '', 'publish', 'open', 'open', '', 'software', '', '', '2008-03-29 13:59:40', '2008-03-29 11:59:40', '', 95, 'http://www.kreisquadratur.de/blog/?page_id=97', 0, 'page', '', 0); 
