INSERT INTO `v4_wp_posts` VALUES (125, 2, '2008-05-05 20:43:06', '2008-05-05 18:43:06', 'Mein eigener <a href="http://www.yubico.com/products/yubikey/">YubiKey</a> von <a href="http://www.yubico.com/">Yubico</a> ist endlich angekommen! Anscheinend stehen die Mädels und Jungs aus Stockholm in Schweden gerade mächtig unter Dampf. Jedenfalls schreibe ich die Tage einen langen und umfangreichen Bericht zu seinen Funktionen (was er kann und ob es klappt. Ja, es klappt! Soviel kann ich jetzt schon verraten), um diesem Produkt auch hier in Deutschland ein Gesicht zu geben und seine Bekannheit zu verbreiten. Wer nicht länger warten kann, hier auch im Podcast über die <a href="http://twit.tv/sn141">RSA Conference</a> bei Security Now! erwähnt (die Jungs um Steve Gibson wollen noch eine eigene Episode darüber drehen).\r\n<!--more-->\r\nAnbei schonmal zwei kleine Fotos:\r\n\r\n<a rel=\'lightbox[slideshow]\' href=\'http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/img_00432.jpg\'><img src="http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/img_00432-150x150.jpg" alt="Yubikey in Verpackung" title="Yubikey" width="150" height="150" class="aligncenter size-thumbnail wp-image-123" /></a> <a  rel=\'lightbox[slideshow]\' href=\'http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/img_0044.jpg\'><img src="http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/img_0044-150x150.jpg" alt="Yubikey an MacBook USB Port" title="Yubikey" width="150" height="150" class="aligncenter size-thumbnail wp-image-124" /></a>\r\n', 'YubiKey angekommen!', 0, '', 'publish', 'open', 'open', '', 'yubikey-von-yubico-angekommen', '', '', '2008-06-16 23:37:01', '2008-06-16 21:37:01', '', 0, 'http://www.kreisquadratur.de/blog/?p=125', 0, 'post', '', 0); 
