INSERT INTO `v4_wp_posts` VALUES (132, 2, '2008-05-21 19:46:39', '2008-05-21 17:46:39', '<img src="http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/kth-logo-150x150.png" alt="" title="KTH Logo" class="reflex itiltnone alignright size-thumbnail wp-image-133" />Ich habe aus Praxisphasen-typischer Langeweile schon mal angefangen, mich mit kleinen Design-Spielchen auf die Herbst \'08-Zeit einzuschießen - mit einem kleinen Ordner-Icon für OS X Leopard welches die schwedische Universität KTH abbildet. Dieser Ordner kann dann im Dock mein BA Ordner-Icon ablösen. <del datetime="2008-06-15T13:33:23+00:00">Wenn ich Lust und Zeit habe, bastel ich noch ein Vista Icon.</del> <!--more-->\r\nDas Icon basiert auf dem Standardordnersymbol ((Das <em>Generic Folder</em> Icon ist  <code>GenericFolderIcon.icns</code> zu finden unter <code>/System/Library/CoreServices/CoreTypes.bundle/Contents/Resources</code>)) in blau mit dem original Logo ((Ich hoffe ich bekomme deshalb keinen Copyright Ärger; es dient ja der Lehre.)) der KTH in der Größe bis 512x512 Pixel.\r\n\r\n<div class="download"><a href=\'http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/kth-folder-icon.icns\'>Download</a></div>\r\n\r\n<a href="http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/kth-folder-icon.png" rel="lightbox"><img src="http://www.kreisquadratur.de/blog/wp-content/uploads/2008/05/kth-folder-icon-300x300.png" alt="KTH OS X Leopard Ordner Icon" title="kth-folder-icon" width="300" height="300" class="aligncenter size-medium wp-image-131" /></a>\r\n\r\n', 'KTH Ordner Icon für OS X Leopard', 0, '', 'publish', 'open', 'open', '', 'kth-ordner-icon-fur-os-x-leopard', '', '', '2008-08-26 20:38:15', '2008-08-26 18:38:15', '', 0, 'http://www.kreisquadratur.de/blog/?p=132', 0, 'post', '', 7); 
